import "./login-component.css";
import Login from "../login/login";
import Register from "../register/register";
import { Row, Col } from "reactstrap";
import "./login-component.css";
import Alertt from "../alert/alert";
import React, { useState } from "react";
import axios from "axios";
import ReacherHelper from "../reacher-helper/reacher-helper";
import ReacherComponent from "../reacher-component/reacher-component";

function LoginComponent() {
  const [showError, setShow] = useState(false);

  const [showRegistrationError, setShow3] = useState(false);
  const [showRegistrationSuccess, setShow4] = useState(false);

  const userLogin = (token) => {
    console.log(token, token.token);
    var resp = [];
    if (token.token) {
      window.location.href = "/loggedin";
    } else {
      setShow(true); //
    }
  };

  const userRegister = (status) => {
    if (status === "error") {
      setShow3(true);
    } else {
      setShow4(true);
    }
  };

  return (
    <div className="LoginComponent">
      <ReacherComponent id={"1"} />
      <Row>
        {" "}
        {showError ? (
          <Alertt className="alert" type={"danger"} message={"login failed"} />
        ) : (
          <></>
        )}
      </Row>

      <Row>
        {" "}
        {showRegistrationError ? (
          <Alertt
            className="alert"
            type={"danger"}
            message={"Registration failed"}
          />
        ) : (
          <></>
        )}
      </Row>

      <Row>
        {" "}
        {showRegistrationSuccess ? (
          <Alertt
            className="alert"
            type={"success"}
            message={"Registration Success"}
          />
        ) : (
          <></>
        )}
      </Row>
      <Row>
        <Col>
          {" "}
          <Login userlogin={userLogin} />{" "}
        </Col>
        <Col>
          {" "}
          <Register userRegister={userRegister} />{" "}
        </Col>
      </Row>
    </div>
  );
}

export default LoginComponent;
