//Logged in reducer

const initialState = {
  state: 0,
  username: "none",
  id: 0,
  jobId: 0,
};
const loggedReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SIGN_IN":
      return { state: 1 };
    case "LOGOUT":
      return { state: 0 };
    default:
      return state;
  }
};
export default loggedReducer;
