import React, { useReducer } from "react";
import "./main-view.css";
import LoginComponent from "../login-component/login-component";
//import SlideShowComponent from "../slide-show/slide-show";
import Carousel from "../carousel/carousel";
import "./main-view.css";
import { Row, Col, Button } from "reactstrap";
import { useSelector, useDispatch } from "react-redux";
import { loggedIn, logout } from "../../actions";

function MainView() {
  const isLogged = useSelector((state) => state.isLogged[0]); //view state
  const dispatch = useDispatch();
  return (
    <div className="MainView">
      <Row>
        <Col>
          <Carousel />
        </Col>
        <h1>Status {isLogged}</h1>
        <Button color="success" onClick={() => dispatch(loggedIn())}>
          login
        </Button>
        <Button color="danger" onClick={() => dispatch(logout())}>
          logout
        </Button>
        <Col>
          <LoginComponent className="login-component" />
        </Col>
      </Row>
      <br />
    </div>
  );
}

export default MainView;
