import React, { useState, useEffect, Component } from "react";
import { Container, Button } from "reactstrap";
import "./helper-component.css";
import axios from "axios";
import { TabPane, Row, Col } from "reactstrap";
import { Table } from "reactstrap";
import { render } from "@testing-library/react";
// import { Form, FormGroup, Label, Input } from "reactstrap";
// import { Link } from "react-router-dom";

class HelperComponent extends Component() {
  name = () => {
    const [items, setItems] = useState([]);
    const [users, setUsers] = useState([]);
    useEffect(() => {
      //setItems({loading:true});
      axios
        .all([
          axios.get("http://127.0.0.1:8000/api/jobs/"),
          axios.get("http://127.0.0.1:8000/api/users/"),
        ])
        .then((res) => {
          //console.log(res);
          setItems(res[0].data);
          setUsers(res[1].data);
          //const items = res.data;
          //setItems({loading:false,res:items})
        })
        .catch((err) => {
          console.log(err);
        });
      //const allItems = items.data;
      //setAppState({loading:false})
    }, []);
  };

  render() {
    return (
      <div className="main-div-helper">
        <Container>
          <TabPane tabId="2">
            <Row>
              <Col sm="3"></Col>
              <Col sm="6">
                <h4>Reachers you can help now!</h4>
                <Table>
                  <thead>
                    <tr>
                      <th>Job Description</th>
                      <th></th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      {/* 
                      <td className="td-1">
                        {this.name.users.map((user) => (
                          <p key={user.id}>{user.location}</p>
                        ))}
                      </td>*/}
                      <td className="td-2">
                        {this.name.items.map((item) => (
                          <p key={item.id}>
                            {item.itemdesc}{" "}
                            <input
                              id={item.id}
                              type="checkbox"
                              value={item.id}
                              onClick={() => console.log(item.id)}
                            ></input>
                          </p>
                        ))}
                      </td>
                    </tr>
                  </tbody>
                  <Button> Check </Button>
                </Table>
              </Col>
              <Col sm="3"></Col>
            </Row>
          </TabPane>
        </Container>
      </div>
    );
  }
}
export default HelperComponent;
