import React, { Component } from "react";
import "./css-components/login-signup.css";

class Login extends Component {
  loginstate = {
    credentials: { username: "", password: "" },
  };

  state = {
    credentials: { username: "", password: "" },
  };

  login = (event) => {
    console.log(this.loginstate.credentials);
    fetch("http://127.0.0.1:8000/auth/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(this.loginstate.credentials),
    })
      .then((data) => data.json()) //append the token to data
      .then((data) => {
        this.props.userlogin(data.token); //pass the token to app.js
      })
      .catch((error) => console.error(error));
  };

  register = (event) => {
    console.log(this.state.credentials);
    fetch("http://127.0.0.1:8000/api/users/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(this.state.credentials),
    })
      .then((data) => data.json()) //append the token to data
      .then((data) => {
        console.log(data);
      })
      .catch((error) => console.error(error));
  };

  inputChanged = (event) => {
    const cred = this.loginstate.credentials;
    cred[event.target.name] = event.target.value;
    this.setState({ credentials: cred });
  };

  inputChangedRegister = (event) => {
    const cred = this.state.credentials;
    cred[event.target.name] = event.target.value;
    this.setState({ credentials: cred });
  };

  render() {
    return (
      <div className="App">
        <div className="login-home">
          <h1>Login user</h1>

          <label>Username</label>

          <input
            type="text"
            name="username"
            value={this.loginstate.credentials.username}
            onChange={this.inputChanged}
          />

          <br />

          <label>Password</label>

          <input
            type="text"
            name="password"
            value={this.loginstate.credentials.password}
            onChange={this.inputChanged}
            required
          />

          <br />

          <button onClick={this.login}>Login</button>
        </div>

        <div className="register-home">
          <h1>Register user</h1>

          <label>Username</label>

          <input
            type="text"
            name="username"
            value={this.state.credentials.username}
            onChange={this.inputChangedRegister}
          />

          <br />

          <label>Password</label>

          <input
            type="text"
            name="password"
            value={this.state.credentials.password}
            onChange={this.inputChangedRegister}
            required
          />

          <br />

          <button onClick={this.register}>Register</button>
        </div>
      </div>
    );
  }
}

export default Login;
