//Logged in reducer

const initialState = {
  state: 0,
  username: "none",
  id: 0,
  jobId: 0,
};
const loggedReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SIGN_IN":
      return {
        ...state,
        state: 1,
        username: "darshan",
      };
    case "LOGOUT":
      return {
        ...state,
        state: 0,
        username: "none",
        id: 0,
        jobId: 0,
      };
    default:
      return state;
  }
};
export default loggedReducer;
