import React, { useState } from "react";
import {
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
  Button,
  Row,
  Col,
} from "reactstrap";
import { Table } from "reactstrap";
import { Form, FormGroup, Label, Input } from "reactstrap";
import classnames from "classnames";
import "./reacher-helper.css";
import axios from "axios";
import { Link } from "react-router-dom";

const ReacherHelper = (props) => {
  const [activeTab, setActiveTab] = useState("1");

  const toggle = (tab) => {
    if (activeTab !== tab) setActiveTab(tab);
  };

  var username = localStorage.getItem("username");
  var username_modified = username.charAt(0).toUpperCase() + username.slice(1);

  axios.get("http://127.0.0.1:8000/api/users/").then((response) => {
    for (var i = 0; i < response.data.length; i++) {
      if (response.data[i].username === username) {
        localStorage.setItem("id", response.data[i].id);
        console.log(response.data[i].id, response.data[i].username);
      }
    }
  });

  var id = localStorage.getItem("id");

  return (
    <div id="main-div">
      <div className="text">
        So what do you feel like doing today {username_modified}
        {id}?
      </div>
      <Nav tabs className="nav">
        <NavItem className="nav-item-1">
          <NavLink
            className={classnames({ active: activeTab === "1" })}
            onClick={() => {
              toggle("1");
            }}
          >
            Reacher
          </NavLink>
        </NavItem>
        <NavItem className="nav-item-2">
          <NavLink
            className={classnames({ active: activeTab === "2" })}
            onClick={() => {
              toggle("2");
            }}
          >
            Helper
          </NavLink>
        </NavItem>
      </Nav>

      <TabContent activeTab={activeTab}>
        <TabPane tabId="2">
          <Row>
            <Col sm="3"></Col>
            <Col sm="6">
              <h4>Reachers you can help now!</h4>
              <Table>
                <thead>
                  <tr>
                    <th>Location of Reachers</th>
                    <th>Job Description</th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>Location 1</td>
                    <td>Buy Groceries @ 5$</td>
                    <td>
                      <Form>
                        <FormGroup check>
                          <Input type="checkbox" />{" "}
                        </FormGroup>
                      </Form>
                    </td>
                  </tr>

                  <tr>
                    <td>Loaction 2</td>
                    <td>Move furniture @ 10$</td>
                    <td>
                      <Form>
                        <FormGroup check>
                          <Input type="checkbox" />{" "}
                        </FormGroup>
                      </Form>
                    </td>
                  </tr>
                </tbody>
              </Table>
            </Col>
            <Col sm="3"></Col>
          </Row>
        </TabPane>
      </TabContent>
    </div>
  );
};

export default ReacherHelper;
