import React, { Component } from "react";
import "./login-signup.css";
import { Button } from "reactstrap";

class Register extends Component {
  state = {
    credentials2: {
      username: "",
      password: "",
      location: "",
      phone: "",
      role: "none",
    },
  };
  register = (event) => {
    console.log(this.state.credentials2);
    fetch("http://127.0.0.1:8000/api/users/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(this.state.credentials2),
    })
      .then((data) => data.json()) //append the token to data
      .then((data) => {
        console.log(data);
      })
      .catch((error) => console.error(error));
  };

  render() {
    return (
      <div className="Login">
        <div className="register-home">
          <h1>Register</h1>

          <label>Username</label>
          <br />
          <input
            type="text"
            name="username"
            value={this.state.credentials2.username}
            onChange={this.inputChangedRegister}
          />

          <br />
          <label>Phone</label>
          <br />
          <input
            type="text"
            name="phone"
            value={this.state.credentials2.phone}
            onChange={this.inputChangedRegister}
          />

          <br />
          <label>Location</label>
          <br />

          <select
            name="location"
            value={this.state.credentials2.location}
            onChange={this.inputChangedRegister}
          >
            <option value="Piplod">Piplod</option>
            <option value="Ichchanath">Ichchanath</option>
            <option value="Vesu">Vesu</option>
            <option value="VIP Road">VIP Road</option>
            <option value="Airport">Airport</option>
          </select>

          <br />

          <label>Password</label>
          <br />
          <input
            type="password"
            name="password"
            value={this.state.credentials2.password}
            onChange={this.inputChangedRegister}
            required
          />

          <br />
          <br />

          <Button color="info" onClick={this.register}>
            Register
          </Button>
        </div>
      </div>
    );
  }
}

export default Register;
