import React, { useState } from "react";
import "./progress-helper.css";
import axios from 'axios';

// ID received from Rachita's code:
var id = 2;

const Filler = (props) => {
  return (<div className="filler" style={{ width: `${props.percentage}%` }} />);
};

const ProgressBar = (props) => {
  const [] = useState(true); // what does this do?

  return (
    <div className="progress-bar">
      <Filler percentage={props.percentage} />
    </div>
  );
};

class ProgressBarExample extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      percentage: 0
    }

    this.nextStep = this.nextStep.bind(this)
    this.finalStep = this.finalStep.bind(this)
    this.exampleReqs = this.exampleReqs.bind(this)
  }

  nextStep() {
    this.setState({ percentage: 50 })
    this.exampleReqs();
  }

  finalStep() {
    this.setState({ percentage: 100 })
    this.finalReqs();
  }

  exampleReqs() {
    axios.patch("http://127.0.0.1:3000/items/"+id+"/", {
      status: 'inprog'
    })
    .then((response) => {
      console.log(response);
    }, (error) => {
      console.log(error);
    });
  }

  finalReqs() {
    axios.patch("http://127.0.0.1:3000/items/"+id+"/", {
      status: 'completed'
    })
    .then((response) => {
      console.log(response);
    }, (error) => {
      console.log(error);
    });
  }

  render() {
    return (
      <div>
        <ProgressBar percentage={this.state.percentage} />

        <div style={{ marginTop: '20px' }}>
          <button
            onClick={this.nextStep}
          >
          In Progress
          </button>
        </div>

        <div style={{ marginTop: '20px' }}>
          <button
            onClick={this.finalStep}
          >
          Complete
          </button>
        </div>

        <div style={{marginTop: '10px', color: 'blue', marginBottom: '15px'}}
        onClick={() => this.setState({ percentage: 0 })}>
        Reset
        </div>

      </div>
    );
  };
};

export default ProgressBarExample; // Exported here

