//Logged in reducer

const initialState = {
  state: 0,
  token: "none",
  username: "none",
  id: 0,
  jobId: 0,
  role: "",
};
const loggedReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SIGN_IN":
      return {
        ...state,
        state: 1,
        token: action.info.token,
        username: action.info.username,
        id: action.info.id,
        jobId: action.info.jobId,
        role: action.info.role,
      };
    case "LOGOUT":
      return {
        ...state,
        state: 0,
        username: "none",
        id: 0,
        jobId: 0,
      };
    default:
      return state;
  }
};
export default loggedReducer;
