import React, { useState } from "react";
import { Nav, NavItem, NavLink } from "reactstrap";
import "./reacher-helper.css";
import axios from "axios";
import { Link } from "react-router-dom";

import { useSelector, useDispatch } from "react-redux";
import { setUserDetails, logout, loggedIn } from "../../actions/loginAction";

const ReacherHelper = (props) => {
  //set login state - redux
  const dispatch = useDispatch();
  dispatch(loggedIn(JSON.parse(localStorage.getItem("userDetails")))); //set the details from local storage

  var username = useSelector((state) => state.isLogged["username"]); //get username from store
  //var username = localStorage.getItem("username");
  var username_modified = username.charAt(0).toUpperCase() + username.slice(1);
  //var username_modified = username;

  console.log("details", localStorage.getItem("userDetails")); //Verify the details in local storage

  dispatch(setUserDetails("1"));

  axios.get("http://127.0.0.1:8000/api/users/").then((response) => {
    for (var i = 0; i < response.data.length; i++) {
      if (response.data[i].username === username) {
        //dispatch(setUserDetails(response.data[i].id)); //set user id in store
        //localStorage.setItem("id", response.data[i].id);
        //console.log(response.data[i].id, response.data[i].username);
      }
    }
  });

  var id = useSelector((state) => state.isLogged["id"]);
  console.log("This id is from store", id);

  return (
    <div id="main-div">
      <div className="text">
        So what do you feel like doing today <u>{username_modified}</u> ?
      </div>
      <Nav tabs className="nav">
        <NavItem className="nav-item-1">
          <Link to="/loggedin/reacher">
            <NavLink>Reacher</NavLink>
          </Link>
        </NavItem>
        <NavItem className="nav-item-2">
          <Link to="/loggedin/helper">
            <NavLink>Helper</NavLink>
          </Link>
        </NavItem>
      </Nav>
    </div>
  );
};

export default ReacherHelper;
