import React, { useState, setState } from "react";
import { Container } from "reactstrap";
import "./reacher-component.css";
import axios from "axios";
import { TabPane, Button, Row, Col } from "reactstrap";
import { Table } from "reactstrap";
import { Form, FormGroup, Label, Input } from "reactstrap";
import { Link } from "react-router-dom";

const ReacherComponent = (props) => {
  axios.get("http://127.0.0.1:8000/api/users/").then((response) => {
    console.log(response.data[0]);
  });

  const serviceState = {
    serviceDetails: {
      itemdesc: "",
      reacher_id: "1",
    },
  };

  const postService = (event) => {
    console.log(serviceState.serviceDetails);

    /*
    fetch("http://127.0.0.1:8000/auth/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(serviceState.serviceDetails),
    })
      .then((data) => data.json()) //append the token to data
      .then((data) => {
        localStorage.setItem("username", this.loginstate.credentials.username); //set username
        this.props.userlogin(data); //pass the token to app.js
      })
      .catch((error) => console.error(error));*/
  };

  //service event
  const inputChanged = (event) => {
    const cred = serviceState.serviceDetails;
    cred[event.target.name] = event.target.value;
    setState({ serviceDetails: cred });
  };

  return (
    <div className="main-div-reacher">
      <Container>
        <TabPane tabId="1">
          <Row>
            <Col sm="12">
              <h4>What service do you require?</h4>
            </Col>
          </Row>
          <Row>
            <Col sm="3"></Col>
            <Col sm="6">
              <Table>
                <thead>
                  <tr>
                    <th>Enter Job Description</th>
                    <th>Enter Current Location</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>
                      <Form>
                        <FormGroup>
                          <Label for="jobDescription"></Label>
                          <Input
                            type="text"
                            name="itemdesc"
                            id="itemdesc"
                            placeholder="Please specify the type of service you require!"
                            value={serviceState.serviceDetails.itemdesc}
                            onChange={inputChanged}
                          />
                        </FormGroup>
                      </Form>
                    </td>
                    <td>
                      <Form>
                        <FormGroup>
                          <Label for="Location"></Label>
                          <Input
                            type="text"
                            name="location"
                            id="location"
                            placeholder="Where are you currently?"
                          />
                        </FormGroup>
                      </Form>
                    </td>
                  </tr>
                </tbody>
              </Table>
              <Link to="/loggedin/reacher">
                <Button onClick={postService}>Find Helpers!</Button>
              </Link>
            </Col>
            <Col sm="3"></Col>
          </Row>
        </TabPane>
      </Container>
    </div>
  );
};

export default ReacherComponent;
